{"customStyles":"/** * @summary Initialize hero * * @name nds-hero * @selector .nds-hero * @modifier */
 .nds-hero {
	 width: 100%;
	/** * @summary Selector for a small hero img * * @name nds-hero_small * @selector .nds-hero_small * @modifier */
}
 .nds-hero_small {
	 display: none;
}
 .nds-hero img {
	 width: 100%;
}
 @media screen and (max-width: 30rem) {
	 .nds-hero {
		 overflow: hidden;
		/** * @summary Selector for a large hero img * * @name nds-hero_large * @selector .nds-hero_large * @modifier */
	}
	 .nds-hero_large {
		 display: none;
	}
	 .nds-hero img {
		 width: fit-content;
		 max-width: fit-content;
		 right: 6rem;
		 position: relative;
	}
	 .nds-hero > img.nds-hero_small {
		 display: block;
		 right: 0;
		 max-width: 100%;
		 width: 100%;
	}
}
